{
        HAL_CAM_Result_en_t result = HAL_CAM_SUCCESS;
        sCamI2cStatus = HAL_CAM_SUCCESS;
        UInt8 bytes;
		bytes = data;
        result |= CAM_WriteI2c(sub_addr, 1, &bytes);
        if (result != HAL_CAM_SUCCESS) {
                sCamI2cStatus = result;
                //pr_debug("stv0987_write(): ERROR: at addr:0x%x with value: 0x%x\n", sub_addr, data);
        }
        return result;
}
static UInt16 stv0987_read(unsigned int sub_addr)
{
	HAL_CAM_Result_en_t result = HAL_CAM_SUCCESS;
	sCamI2cStatus = HAL_CAM_SUCCESS;
	//UInt16 data;
	//UInt16 temp;
	UInt8 data;

	result |= CAM_ReadI2c(sub_addr, 1, (UInt8 *) &data);
	if (result != HAL_CAM_SUCCESS) {
		sCamI2cStatus = result;
		pr_info("stv0987_read(): ERROR: %d\r\n", result);
	}

	//	temp = data;
	//	data = ((temp & 0xFF) << 8) | ((temp & 0xFF00) >> 8);

	return data;
}



