                      peaking_gain = 0x02;
                      jpeg_file_size = 40;            // 40 Kbytes
                      stv0987_write(bStillCustomImageResolution_Xsize_lo,(240 & 0x00FF));  // SystemOutputImageDimensionsPage_bStillCustomImageResolution_Xsize_lo
                      stv0987_write(bStillCustomImageResolution_Xsize_hi,(240 >> 8));      // SystemOutputImageDimensionsPage_bStillCustomImageResolution_Xsize_hi
                      stv0987_write(bStillCustomImageResolution_Ysize_lo,((320) & 0x00FF));  // SystemOutputImageDimensionsPage_bStillCustomImageResolution_Ysize_lo
                      stv0987_write(bStillCustomImageResolution_Ysize_hi,((320) >> 8));      // SystemOutputImageDimensionsPage_bStillCustomImageResolution_Ysize_hi
                      break;
    case CAMDRV_RESOLUTION_QVGA:          // 0x54 QVGA            320x240
                      resolution = 0x54;
                      peaking_gain = 0x02;
                      jpeg_file_size = 40;            // 40 Kbytes
                      break;
    case CAMDRV_RESOLUTION_CIF:              // 0x55 CIF             352x288
                      resolution = 0x55;
                      peaking_gain = 0x02;
                      jpeg_file_size = 40;            // 40 Kbytes
                      break;
    case CAMDRV_RESOLUTION_320x480:              // 320x480
                      peaking_gain = 0x03;
                      jpeg_file_size = 40;            // 40 Kbytes
                      resolution = 0x60;
                      stv0987_write(bStillCustomImageResolution_Xsize_lo,(320 & 0x00FF));  // SystemOutputImageDimensionsPage_bStillCustomImageResolution_Xsize_lo
                      stv0987_write(bStillCustomImageResolution_Xsize_hi,(320 >> 8));      // SystemOutputImageDimensionsPage_bStillCustomImageResolution_Xsize_hi
