                      resolution = 0x60;
                      stv0987_write(bStillCustomImageResolution_Xsize_lo,(640 & 0x00FF));  // SystemOutputImageDimensionsPage_bStillCustomImageResolution_Xsize_lo
                      stv0987_write(bStillCustomImageResolution_Xsize_hi,(640 >> 8));      // SystemOutputImageDimensionsPage_bStillCustomImageResolution_Xsize_hi
                      stv0987_write(bStillCustomImageResolution_Ysize_lo,(240 & 0x00FF));  // SystemOutputImageDimensionsPage_bStillCustomImageResolution_Ysize_lo
                      stv0987_write(bStillCustomImageResolution_Ysize_hi,(240 >> 8));      // SystemOutputImageDimensionsPage_bStillCustomImageResolution_Ysize_hi
                      break;
    case CAMDRV_RESOLUTION_VGA:              // 0x56 VGA             640x480
			pr_debug("!!!!!!!!! COnfiguring VGA\n");
                      resolution = 0x56;
                      peaking_gain = 0x04;
                      jpeg_file_size = 80;            // 80 Kbytes
                      break;
    case CAMDRV_RESOLUTION_SVGA:             // 0x57 SVGA            800x600
                      resolution = 0x57;
                      peaking_gain = 0x05;
                      jpeg_file_size = 120;           // 120 Kbytes
                      break;
    case CAMDRV_RESOLUTION_XGA:              // 0x60 CUSTOM          1024x768
                      peaking_gain = 0x0B;
                      jpeg_file_size = 192;           // 10 Kbytes
                      resolution = 0x60;
                      stv0987_write(bStillCustomImageResolution_Xsize_lo,(CamWindowSize_XGA_W & 0x00FF));  // SystemOutputImageDimensionsPage_bStillCustomImageResolution_Xsize_lo
                      stv0987_write(bStillCustomImageResolution_Xsize_hi,(CamWindowSize_XGA_W >> 8));      // SystemOutputImageDimensionsPage_bStillCustomImageResolution_Xsize_hi
                      stv0987_write(bStillCustomImageResolution_Ysize_lo,(CamWindowSize_XGA_H & 0x00FF));  // SystemOutputImageDimensionsPage_bStillCustomImageResolution_Ysize_lo
                      stv0987_write(bStillCustomImageResolution_Ysize_hi,(CamWindowSize_XGA_H >> 8));      // SystemOutputImageDimensionsPage_bStillCustomImageResolution_Ysize_hi
                      break;
    case CAMDRV_RESOLUTION_SXGA:             // 0x58 SXGA            1280x1024  (1.3MP)
