                      peaking_gain = 0x0B;
                      jpeg_file_size = 320;           // 320 Kbytes

					  stv0987_write(bStillCustomImageResolution_Xsize_lo,(1280 & 0x00FF));  // SystemOutputImageDimensionsPage_bStillCustomImageResolution_Xsize_lo
