            break;
        case CAMDRV_IMAGE_YUV422:                   ///< 101 = capture YCbCr format
			pr_debug("!!!!!!!!! COnfiguring YUV422\n");
            format = 0x65;
            break;
        case CAMDRV_IMAGE_JPEG:                    ///< 102 = capture JPEG format
		default:
            format = 0x66;
            // JPEG Configuration Settings
            stv0987_write(bSqueezeSettings,0xA1); // JPEGImageCharateristicsControlParameters_bSqueezeSettings
             // Set the JPEG still image quality to high
             CAMDRV_SetImageQuality(ImageSettingsConfig_st.sensor_jpegQuality->default_setting, sCamSensor);
           	jpeg_file_size = jpeg_file_size * 5;
