//****************************************************************************
//
// Function Name:   Result_t CAMDRV_SensorSetSleepMode()
//
// Description: This function sets the ISP in Sleep Mode
//
// Notes:
//
//****************************************************************************
HAL_CAM_Result_en_t CAMDRV_SensorSetSleepMode(void)
{
    UInt8 register_value;
    HAL_CAM_Result_en_t result = HAL_CAM_SUCCESS;

    //---------Read bSystemStatus
    register_value = stv0987_read(bSystemStatus);
    ////pr_debug("CAMDRV_SensorSetSleepMode(): bSystemStatus = %d \r\n", register_value);

    if (register_value != STATE_SLEEP)
    {
        if (register_value != STATE_IDLE)
        {

       // bSystemControl possible values:
       // 0x49=73   98x enters in STATE_UNINITIALIZED
       // 0x39=57 98x enters in STATE_BOOT
       // 0x11=17 98x enters in STATE_SLEEP
       // 0x12=18 98x enters in STATE_IDLE
       // 0x13=19 98x enters in STATE_VIEWFINDER
       // 0x14=20 98x enters in STATE_STILLS
       // 0x15=21 98x enters in STATE_MOVIE
       // 0x16=22 98x enters in STATE_STILL_N_THUMBNAIL
       // 0x18=24 98x enters in STATE_BUSY
            stv0987_write(bSystemControl, STATE_IDLE);                              // SystemControlParameters_bSystemControl Idle mode
            stv0987_read(bSystemStatus);                                                        // read bSystemStatus for I2C write to complete
            result = cam_WaitValue(2000,bSystemStatus,STATE_IDLE);  // Poll system status to confirm Idle state.
            if (result != HAL_CAM_SUCCESS)
            {
                //pr_debug("CAMDRV_SensorSetSleepMode(): STATE_IDLE:  ERROR: \r\n");
            }
        }
       // bSystemControl possible values:
       // 0x49=73   98x enters in STATE_UNINITIALIZED
       // 0x39=57 98x enters in STATE_BOOT
       // 0x11=17 98x enters in STATE_SLEEP
       // 0x12=18 98x enters in STATE_IDLE
       // 0x13=19 98x enters in STATE_VIEWFINDER
       // 0x14=20 98x enters in STATE_STILLS
       // 0x15=21 98x enters in STATE_MOVIE
       // 0x16=22 98x enters in STATE_STILL_N_THUMBNAIL
       // 0x18=24 98x enters in STATE_BUSY
        stv0987_write(bSystemControl, STATE_SLEEP);                              // SystemControlParameters_bSystemControl
        result = cam_WaitValue(5000,bSystemStatus,STATE_SLEEP);  // Poll system status to confirm state.
        if (result != HAL_CAM_SUCCESS)
        {
            //pr_debug("CAMDRV_SensorSetSleepMode(): STATE_SLEEP:  ERROR: \r\n");
        }
    }

    return result;
}


