//****************************************************************************
//
// Function Name:   Result_t CAMDRV_DisableCapture(CamSensorSelect_t sensor)
//
// Description: This function halts camera video capture
//
// Notes:
//                  ViewFinder -> IDLE
//                  Movie -> IDLE
//                  Stills -> IDLE
//****************************************************************************
HAL_CAM_Result_en_t CAMDRV_DisableCapture(CamSensorSelect_t sensor)
{
    UInt8 register_value,error_value;
    HAL_CAM_Result_en_t result = HAL_CAM_SUCCESS;

//---------Read bSystemStatus
    register_value = stv0987_read(bSystemStatus);
    ////pr_debug("CAMDRV_DisableCapture(): bSystemStatus = %d \r\n",register_value);
        if (register_value != STATE_SLEEP)
        {
            if (register_value != STATE_IDLE)
            {

           // bSystemControl possible values:
           // 0x49=73   98x enters in STATE_UNINITIALIZED
           // 0x39=57 98x enters in STATE_BOOT
           // 0x11=17 98x enters in STATE_SLEEP
           // 0x12=18 98x enters in STATE_IDLE
           // 0x13=19 98x enters in STATE_VIEWFINDER
           // 0x14=20 98x enters in STATE_STILLS
           // 0x15=21 98x enters in STATE_MOVIE
           // 0x16=22 98x enters in STATE_STILL_N_THUMBNAIL
           // 0x18=24 98x enters in STATE_BUSY
                stv0987_write(bSystemControl, STATE_IDLE);                              // SystemControlParameters_bSystemControl Idle mode
                stv0987_read(bSystemStatus);                                                        // read bSystemStatus for I2C write to complete
                result = cam_WaitValue(1000,bSystemStatus,STATE_IDLE);  // Poll system status to confirm Idle state.
                if (result != HAL_CAM_SUCCESS)
                {
                    register_value = stv0987_read(bSystemStatus);
                    error_value = stv0987_read(bSystemErrorStatus);
                    //pr_debug("CAMDRV_DisableCapture(): ERROR #1:  STATE_IDLE Failed: bSystemStatus = %d bSystemErrorStatus = %d \r\n", register_value,error_value);
                // Try 2nd time
                    stv0987_write(bSystemControl, STATE_IDLE);                              // SystemControlParameters_bSystemControl Idle mode
                    stv0987_read(bSystemStatus);                                                        // read bSystemStatus for I2C write to complete
                    result = cam_WaitValue(1000,bSystemStatus,STATE_IDLE);  // Poll system status to confirm Idle state.
                    register_value = stv0987_read(bSystemStatus);
                    error_value = stv0987_read(bSystemErrorStatus);
                    //pr_debug("CAMDRV_DisableCapture(): ERROR #2:  STATE_IDLE Failed: bSystemStatus = %d bSystemErrorStatus = %d \r\n", register_value,error_value);
                }
            }
        }


    return result;
}


