//****************************************************************************
//
// Function Name:   Result_t CAMDRV_CfgStillnThumbCapture(CamImageSize_t image_resolution, CAMDRV_IMAGE_TYPE_T format, CamSensorSelect_t sensor)
//
// Description: This function configures Stills Capture
//
// Notes:
//
//****************************************************************************
HAL_CAM_Result_en_t CAMDRV_CfgStillnThumbCapture(
        CamImageSize_t stills_resolution,
        CamDataFmt_t stills_format,
        CamImageSize_t thumb_resolution,
        CamDataFmt_t thumb_format,
        CamSensorSelect_t sensor
        )

{
    UInt8 register_value = 0,error_value = 0;
    HAL_CAM_Result_en_t result = HAL_CAM_SUCCESS;

//---------Read bSystemStatus
    register_value = stv0987_read(bSystemStatus);

    if (register_value != STATE_IDLE)
    {
    // ---------Idle Mode
    // bSystemControl possible values:
    // 0x49=73   98x enters in STATE_UNINITIALIZED
    // 0x39=57 98x enters in STATE_BOOT
    // 0x11=17 98x enters in STATE_SLEEP
    // 0x12=18 98x enters in STATE_IDLE
    // 0x13=19 98x enters in STATE_VIEWFINDER
    // 0x14=20 98x enters in STATE_STILLS
    // 0x15=21 98x enters in STATE_MOVIE
    // 0x16=22 98x enters in STATE_STILL_N_THUMBNAIL
    // 0x18=24 98x enters in STATE_BUSY
        stv0987_write(bSystemControl, STATE_IDLE);                  // SystemControlParameters_bSystemControl Idle mode
        stv0987_read(bSystemStatus);                                // read bSystemStatus for I2C write to complete
        result = cam_WaitValue(2000,bSystemStatus,STATE_IDLE);  // Poll system status to confirm Idle state.
        if (result != HAL_CAM_SUCCESS)
        {
            register_value = stv0987_read(bSystemStatus);
            error_value = stv0987_read(bSystemErrorStatus);
            //pr_debug("CAMDRV_CfgStillnThumbCapture(): ERROR:  STATE_IDLE Failed: bSystemStatus = %d bSystemErrorStatus = %d \r\n", register_value,error_value);
        }
    }


    VB6953_10b_Idle_Before_STILLS();

    // Set up Stills only for the time being
    //we will see thumb capture later : Fix me
    result = stv098x_SensorSetFrameRate(15, sensor);               // 15 Fps for Stills Capture
#if defined (CONFIG_CAM_CSI2)
    stv098x_SensorSetPreviewMode(thumb_resolution,thumb_format); // comment: vinay for jpeg alone 1
#endif
    stv098x_SensorSetCaptureMode(stills_resolution, stills_format, sensor);
    stv098x_SensorSetInterLeaveMode(CamCaptureStill, stills_format);
#if defined (CONFIG_CAM_CSI2)
    stv0987_write(bInterleavingMode, 160);	// comment: vinay for jpeg alone 2
#endif
   // bSystemControl possible values:
   // 0x49=73   98x enters in STATE_UNINITIALIZED
   // 0x39=57 98x enters in STATE_BOOT
   // 0x11=17 98x enters in STATE_SLEEP
   // 0x12=18 98x enters in STATE_IDLE
   // 0x13=19 98x enters in STATE_VIEWFINDER
   // 0x14=20 98x enters in STATE_STILLS
   // 0x15=21 98x enters in STATE_MOVIE
   // 0x16=22 98x enters in STATE_STILL_N_THUMBNAIL
   // 0x18=24 98x enters in STATE_BUSY

   stv0987_write(bSystemControl, STATE_STILLS);                             // SystemControlParameters_bSystemControl Stills mode
   stv0987_read(bSystemStatus);                                                             // read bSystemStatus for I2C write to complete
   result = cam_WaitValue(2000,bSystemStatus,STATE_STILLS);     // Poll system status to confirm Stills state.
    if (result != HAL_CAM_SUCCESS)
    {
        register_value = stv0987_read(bSystemStatus);
        error_value = stv0987_read(bSystemErrorStatus);
        //pr_debug("CAMDRV_CfgStillnThumbCapture(): ERROR:  STATE_STILLS Failed: bSystemStatus = %d bSystemErrorStatus = %d \r\n", register_value,error_value);
    }


    VB6953_scene_auto();
    return result;
}

