/****************************************************************************
/ Function Name:   HAL_CAM_Result_en_t CAMDRV_SetWBMode(CamWB_WBMode_t wb_mode)
/
/ Description: This function will set the white balance of camera
/ Notes:
****************************************************************************/

HAL_CAM_Result_en_t CAMDRV_SetWBMode(CamWB_WBMode_t wb_mode,
				     CamSensorSelect_t sensor)
{
	HAL_CAM_Result_en_t result = HAL_CAM_SUCCESS;
    stv0987_write(0x2680, 0x20);     //WhiteBalanceStatisticsControls bLowThreshold

    switch( wb_mode )
    {
        case CamWB_Off:
            stv0987_write(0x2500, 0x00);     //WhiteBalanceControls bWBMode {OFF}
            break;
        case CamWB_Auto:
            stv0987_write(0x2500, 0x01);     //WhiteBalanceControls bWBMode {AUTOMATIC}
            break;
        case CamWB_Daylight:
            stv0987_write(0x2500, 0x04);     //WhiteBalanceControls bWBMode {DAYLIGHT_PRESET}
            break;
        case CamWB_Tungsten:
            stv0987_write(0x2500, 0x05);     //WhiteBalanceControls bWBMode {TUNGSTEN_PRESET}
            break;
        case CamWB_DaylightFluorescent:
            stv0987_write(0x2500, 0x06);     //WhiteBalanceControls bWBMode {FLUORESCENT_PRESET}
            break;
        case CamWB_Sunset:
            stv0987_write(0x2500, 0x07);     //WhiteBalanceControls bWBMode {HORIZON_PRESET}
            break;
        case CamWB_Flash:
            stv0987_write(0x2500, 0x08);     //WhiteBalanceControls bWBMode {FLASHGUN_PRESET}
            break;
        default:
            wb_mode = CamWB_Auto;

            break;
    }

    if (sCamI2cStatus != HAL_CAM_SUCCESS) {
		pr_debug("CAMDRV_SetWBMode(): Error[%d] \r\n", sCamI2cStatus);
		result = sCamI2cStatus;
	}
    return result;
}

/****************************************************************************
/ Function Name:   HAL_CAM_Result_en_t CAMDRV_SetAntiBanding(
/					CamAntiBanding_t effect)
/
/ Description: This function will set the antibanding effect of camera
/ Notes:
****************************************************************************/

HAL_CAM_Result_en_t CAMDRV_SetAntiBanding(CamAntiBanding_t effect,
					  CamSensorSelect_t sensor)
{
	HAL_CAM_Result_en_t result = HAL_CAM_SUCCESS;

	if ((effect == CamAntiBandingAuto)) {
		stv0987_write(0x2040,0x02);  //Anti Flicker Mode is automatic
		stv0987_write(0x4602, 0x01); //setting it TRUE reduces flicker
	} else if ((effect == CamAntiBandingOff)) {
		stv0987_write(0x2040,0x00);  //Anti Flicker Mode is inhibited
		stv0987_write(0x4602, 0x01); //setting it TRUE reduces flicker
	} else if ((effect == CamAntiBanding50Hz) || (effect == CamAntiBanding60Hz)) {
		stv0987_write(0x4600, 0x3C); //default value = 3C = 60 Hz
		stv0987_write(0x4602, 0x01); //setting it TRUE reduces flicker
	} else {
		stv0987_write(0x2040,0x00);
		stv0987_write(0x4602, 0x01);
	}
	if (sCamI2cStatus != HAL_CAM_SUCCESS) {
		pr_debug("CAMDRV_SetAntiBanding(): Error[%d] \r\n",sCamI2cStatus);
		result = sCamI2cStatus;
	}
	return result;
}

