void VB6953_Auto( void )
{

    stv0987_write(0x0880, 0x04); // HostToSensorAccessControl bRequest {HostToSensor_WriteWord}
    stv0987_write(0x0885, 0x0b); // HostToSensorAccessControl uwSensorIndex {see below}
    stv0987_write(0x0886, 0x88); // HostToSensorAccessControl uwSensorIndex {2952}
    stv0987_write(0x0981, 0x80); // HostToSensorAccessData uwDataLow {see below}
    stv0987_write(0x0982, 0x00); // HostToSensorAccessData uwDataLow {32768}
    stv0987_write(0x0882, 0x01); // HostToSensorAccessControl bCommandCoin {TestCoin_Heads}
    cam_WaitValue(200, 0x0900, 0x01); // Polling register  HostToSensorAccessStatus [read only] bStatusCoin , waiting for value {TestCoin_Heads}, timeout after 200 ms
    stv0987_write(0x0882, 0x02); // HostToSensorAccessControl bCommandCoin {TestCoin_Tails}
    cam_WaitValue(200, 0x0900, 0x02); // Polling register  HostToSensorAccessStatus [read only] bStatusCoin , waiting for value {TestCoin_Tails}, timeout after 200 ms

    stv0987_write(0x2500, 0x01); // WhiteBalanceControls bMode {AUTOMATIC}

    stv0987_write(0x3580, 0x00); // ColourMatrixControl fEnableTestModeCMforCE0 {VPIP_FALSE}
    stv0987_write(0x3582, 0x00); // ColourMatrixControl fEnableTestModeCMforCE1 {VPIP_FALSE}


}
