void VB6953_07_Boot_OutputIF_Config_ITU( void )
{

    //*********************************************************************************************
    //** Output interface configuration
    //*********************************************************************************************

    stv0987_write(0x5f08, 0x82);	// SystemImageCharacteristicsControlParameters bOutputDestination {ITU601_EXTNSYNC}

    //** The writes below are only needed for the CSI2 output interface
    stv0987_write(0x5f22, 0x01);	// SystemImageCharacteristicsControlParameters bCSI2LaneCount {DUAL_LANE}
    stv0987_write(0x5f24, 0x01);	// SystemImageCharacteristicsControlParameters fSelectMIPI_DPYHTXSpec {1}

    //** Set PLL enable to ENABLE_PLL (0xb4) if using the CSI2 output
    stv0987_write(0x6180, 0xb4);	// SystemClockControlParameters bPLLEnable {ENABLE_PLL}

    stv0987_write(0x6182, 0xb6);	// SystemClockControlParameters bPLLEnableManualMode {ENABLE_PLL_MANUAL_MODE}
    stv0987_write(0x6184, 0xb4);	// SystemClockControlParameters bSelectClkSys_ClkSource {ENABLE_PLL}
    stv0987_write(0x6186, 0xb4);	// SystemClockControlParameters bSelectClkMisc_ClkSource {ENABLE_PLL}
    stv0987_write(0x618a, 0x17);	// SystemClockControlParameters bPllMultiplier {23}
    stv0987_write(0x6191, 0x01);	// SystemClockControlParameters uwOutputDataRate {see below}
    stv0987_write(0x6192, 0x2B);	// SystemClockControlParameters uwOutputDataRate {299}

    stv0987_write( 0x6102, 0x05 );


    stv0987_write(0x6092, 0x00);	// ITUControlParameters bITUClockRatio {ITU_CLOCK_UNDIVIDED}

    stv0987_write(0x6086, 0x1);	// ITUControlParameters bSelectVsyncPolarity {Polarity_ActiveHigh}
    stv0987_write(0x6088, 0x1);	// ITUControlParameters bSelectHsyncPolarity {Polarity_ActiveHigh}



}

