//
//  Sensor Read Thru Host
//
// ===========================================
HAL_CAM_Result_en_t  stv098x_SensorRead(UInt32 byte_count, UInt16 addr, UInt16 *sensor_data)
{
    HAL_CAM_Result_en_t result = HAL_CAM_SUCCESS;
    volatile UInt8 cmd_coin;
    volatile UInt8 status_coin;
    int request = 0;

    if ((byte_count >2 ) || (byte_count == 0))
    {
       ////pr_debug("stv098x_SensorRead(): ERROR: Invalid  byte_count = %d \r\n", byte_count);
       result = HAL_CAM_ERROR_OTHERS;
    }
    else
    {
        if (byte_count == 1)
        {
            request = 0; /* HostToSensor_ReadByte */
        }
        else
        {
            request = 1; /* HostToSensor_ReadWord */
        }

        // Write status coin
        status_coin = (stv0987_read(bStatusCoin) & 0x0003);
        // command coin == flipped status coin
        cmd_coin = 1;
        if (status_coin == 1)
        {
            cmd_coin = 2;  // flip command coin
        }
        // Set Read Addr
        stv0987_write(bRequest, request);  // HostToSensorAccessControl bRequest {HostToSensor_stv0987_read(byte or word)}
        stv0987_write(uwSensorIndex_MS,(addr >> 8)); // HostToSensorAccessControl uwSensorIndex MSB
        stv0987_write(uwSensorIndex_LS,addr&0xFF);        // HostToSensorAccessControl uwSensorIndex LSB

        // Write command coin
        stv0987_write(bCommandCoin,cmd_coin);        // HostToSensorAccessControl bCommandCoin {TestCoin_Tails/TestCoin_Heads}
        // Poll for status_coin == cmd_coin
        result = cam_WaitValue(2000,bStatusCoin,cmd_coin);  // HostToSensorAccessStatus [read only] bStatusCoin {TestCoin_Tails}

        // Read the bytes now
        if (byte_count == 1)
        {
            *sensor_data = stv0987_read(uwDataLow_LS);
        }
        else
        {
            *sensor_data = (stv0987_read(uwDataLow_MS)<<8) | (stv0987_read(uwDataLow_LS));
        }
    }

    if (result != HAL_CAM_SUCCESS)
    {
        ////pr_debug("stv098x_SensorRead): ERROR:byte_count = %d \r\n",byte_count);
    }
    else
    {
        ////pr_debug("stv098x_SensorRead(): Complete OK, SensorActive = %d, uwDataHigh_MS=0x%x, uwDataHigh_LS=%x, uwDataLow_MS=0x%x, uwDataLow_LS=%x \r\n", stv0987_read(0x0888), stv0987_read(uwDataHigh_MS), stv0987_read(uwDataHigh_LS), stv0987_read(uwDataLow_MS), stv0987_read(uwDataLow_LS));
        ////pr_debug("stv098x_SensorRead(): MasterI2Control: bSensorSerialAddress(0x%x), bMaxNumOfGrabAttempts(0x%x) \r\n", stv0987_read(0x0B80), stv0987_read(0x0B82));
        ////pr_debug("stv098x_SensorRead(): MasterI2Status : fTransactionError(0x%x), bNumTransactionFailures(0x%x) \r\n", stv0987_read(0x0C82), stv0987_read(0x0C84));
        ////pr_debug("stv098x_SensorRead(): HostToSensorAccessStatus : bHostToSensorAccessErrCount(0x%x)\r\n", stv0987_read(0x0902));
    }
    return result;
}

