//***************************************************************************
/**
*       98x Reset
*/
HAL_CAM_Result_en_t stv098x_Reset()
{
    HAL_CAM_Result_en_t result = HAL_CAM_SUCCESS;
    int id = 0,id1 = 0;

    ////pr_debug("stv098x_reset(): \r\n");

   //** Release MCU reset
   stv0987_write(0xb08c, 0x00);

   //** Poll H/W register to confirm 987 is out of reset.
   result = cam_WaitValue(5000, 0xb06c, 0x1f);

   // bSystemControl possible values:
   // 0x49=73 98x enters in STATE_UNINITIALIZED
   // 0x39=57 98x enters in STATE_BOOT
   // 0x11=17 98x enters in STATE_SLEEP
   // 0x12=18 98x enters in STATE_IDLE
   // 0x13=19 98x enters in STATE_VIEWFINDER
   // 0x14=20 98x enters in STATE_STILLS
   // 0x15=21 98x enters in STATE_MOVIE
   // 0x16=22 98x enters in STATE_STILL_N_THUMBNAIL
   // 0x18=24 98x enters in STATE_BUSY
    result = cam_WaitValue(5000, bSystemStatus, STATE_UNINITIALIZED);  // Poll system status to confirm
    if (result != HAL_CAM_SUCCESS)
    {
        ////pr_debug("stv098x_reset(): ERROR:  STATE_UNINITIALIZED Failed \r\n");
    }
    id = stv0987_read(uwDeviceId_MS);
	////pr_debug("Full ID MS read is 0x%x\n",id);
	id = id << 8;
    id1 |= stv0987_read(uwDeviceId_LS);
	////pr_debug("Full ID LS read is 0x%x\n",id1);
	id = id | id1;
	/*
    if (id != STV_DEVICE_ID)
    {
        result = HAL_CAM_ERROR_INTERNAL_ERROR;
        ////pr_debug("stv098x_reset(): ERROR:  Device Id Failed: Id=0x%x, ID Read= 0x%x \r\n", STV_DEVICE_ID, id);
    }
	*/


   return result;
}


