// ===========================================
//
//  Sensor Write Thru Host
//
// ===========================================
HAL_CAM_Result_en_t stv098x_SensorWrite(UInt16 byte_count, UInt16 addr, UInt8 *sensor_data)
{
    HAL_CAM_Result_en_t result = HAL_CAM_SUCCESS;
    UInt8 cmd_coin, status_coin;
    UInt16 count = 0;


    while ( (result == HAL_CAM_SUCCESS) && (count < byte_count) )
    {
        // Write status coin
        status_coin = (stv0987_read(bStatusCoin) & 0x0003);
        // command coin == flipped status coin
        cmd_coin = 1;
        if (status_coin == 1)
        {
            cmd_coin = 2;                                    // flip command coin
        }
        // Set Write Addr
        stv0987_write(bRequest,0x03);                            // HostToSensorAccessControl bRequest {HostToSensor_stv0987_write}
        stv0987_write(uwSensorIndex_MS,(addr >> 8));            // HostToSensorAccessControl uwSensorIndex MSB
        stv0987_write(uwSensorIndex_LS,addr);                    // HostToSensorAccessControl uwSensorIndex LSB
        stv0987_write(uwDataLow_MS,0x00);                        // HostToSensorAccessData uwDataLow MSB
        stv0987_write(uwDataLow_LS,(*sensor_data++ & 0x00FF));  // HostToSensorAccessData uwDataLow LSB
        // Write command coin
        stv0987_write(bCommandCoin,cmd_coin);                    // HostToSensorAccessControl bCommandCoin {TestCoin_Tails/TestCoin_Heads}
        // Poll for status_coin == cmd_coin
        result = cam_WaitValue(2000,bStatusCoin,cmd_coin);  // HostToSensorAccessStatus [read only] bStatusCoin {TestCoin_Tails}
        // Reset status coin & update data byte, byte count, addr
        status_coin = cmd_coin;
        count++;
        addr++;
    }

    if (result != HAL_CAM_SUCCESS)
    {
        ////pr_debug("stv098x_SensorWrite(): ERROR: count write = %d byte_count = %d \r\n", count,byte_count);
    }
    else
    {
        ////pr_debug("stv098x_SensorWrite(): Complete OK \r\n");
    }
    return result;
}



