void VB6953_Preview_15fps(void)
{
    stv0987_write(0x0882, 0x02); // HostToSensorAccessControl bCommandCoin {TestCoin_Tails}
    cam_WaitValue(200, 0x0900, 0x02); // Polling register "HostToSensorAccessStatus [read only] bStatusCoin" for value {TestCoin_Tails}, waiting for 200ms
    stv0987_write(0x0882, 0x01); // HostToSensorAccessControl bCommandCoin {TestCoin_Heads}
    cam_WaitValue(200, 0x0900, 0x01); // Polling register "HostToSensorAccessStatus [read only] bStatusCoin" for value {TestCoin_Heads}, waiting for 200ms

    //** Wait 10ms for the 854 firmware to start
    msleep(10);

    stv0987_write(0x0882, 0x02); // HostToSensorAccessControl bCommandCoin {TestCoin_Tails}
    cam_WaitValue(200, 0x0900, 0x02); // Polling register "HostToSensorAccessStatus [read only] bStatusCoin" for value {TestCoin_Tails}, waiting for 200ms


    stv0987_write(0x0880, 0x03); // HostToSensorAccessControl bRequest {HostToSensor_cam_write}

    stv0987_write(0x0885, 0x0B); // HostToSensorAccessControl uwSensorIndex {see below}
    stv0987_write(0x0886, 0x80); // HostToSensorAccessControl uwSensorIndex {2944}

    stv0987_write(0x0981, 0x00); // HostToSensorAccessData uwDataLow {see below}
    stv0987_write(0x0982, 0x02); // HostToSensorAccessData uwDataLow {2}

    stv0987_write(0x0882, 0x01); // HostToSensorAccessControl bCommandCoin {TestCoin_Heads}
    cam_WaitValue(200, 0x0900, 0x01); // Polling register "HostToSensorAccessStatus [read only] bStatusCoin" for value {TestCoin_Heads}, waiting for 200ms

    msleep(500);

}
